    { attrs: { "slot-key": _vm.$parent.slotKey } },
    [
      _c(
        "h1",
        {
          attrs: {
            id: "why-akaunt-is-the-best-for-advanced-inventory-management",
          },
        },
        [
          _c(
            "a",
            {
              staticClass: "header-anchor",
              attrs: {
                href: "#why-akaunt-is-the-best-for-advanced-inventory-management",
              },
